package com.ppusari.java.etc.xroshot.xroshot.auth;

import java.util.Random;

public class AuthKey {

	int index;
	String authKey;

	public AuthKey() {	
	 
		String fullAuthKey = "";
		
		Random random = new Random();
		this.index = random.nextInt(3900); // 0 ~ 3970
		this.authKey = fullAuthKey.substring(index, index+128);	
	}
	
	public String makeAuthTicket(String id, String pw, String endUserId, String serverTime, String onetimekey){		
		return id + "|" + pw + "|" + endUserId + "|" + serverTime + "|" + onetimekey; // 인증티켓
	}
	
	// Getters.
	public int getIndex() {
		return index;
	}
		
	public String getAuthKey() {
		return authKey;
	}
}